import React from 'react';
import { useEffect, useState } from 'react';
import { fetchUsername, getCurrentToken, fetchUserRoutines } from '../api';
import MyRoutine from './MyRoutine';
import Button from '@material-ui/core/Button';
import RoutineForm from './RoutineForm';
import { makeStyles } from '@material-ui/core/styles';


const MyRoutines = ({currentUser, setCurrentUser}) => {

  const [myRoutines, setMyRoutines] = useState([]);
  const [creator, setCreator] = useState('');
  const [open, setIsOpen] = useState(false);

  useEffect(() => {
    Promise.all([fetchUserRoutines(currentUser)]).then(([data]) => {
      setMyRoutines(data);
    });
  }, [])

  console.log(currentUser)


  const showForm = () => {
    if (open === false) {
      setIsOpen(true)
    }
    else {
      setIsOpen(false)
    }
  }

  const useStyles = makeStyles((theme) => ({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
      position: 'relative',
      margin: '10px',
    },
    formControl: {
      margin: theme.spacing(2),
      minWidth: 120,
    },
    textFields: {
      width: '350px',
      margin: theme.spacing(2)
    },
  }));

  const classes = useStyles();

  const token = localStorage.getItem('token');

  console.log(token);
  console.log(myRoutines);


  return (
    <div className='routine-card'>
      <h1>My Routines</h1>
      <Button 
        className={classes.container}
        color="secondary"
        variant="contained"
        type="submit"
        onClick={showForm}>Create A New Routine</Button>
      <RoutineForm currentUser={currentUser} setMyRoutines={setMyRoutines} open={open} />
      {myRoutines ? myRoutines.map((routine, index) =>
        <MyRoutine
          creator={creator}
          setCreator={setCreator}
          key={index} 
          routine={routine} />)
        : <h1>Use the form above to start creating routines.</h1>}
    </div>
  )
}


export default MyRoutines;
