import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import { fetchUsername, fetchUserRoutines } from '../api';
import createRoutine from './Routines'
import './style.css';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';


const RoutineForm = ({open}) => {

    const [routines, setRoutines] = useState([]);
    const [routineName, setRoutineName] = useState('');
    const [routineGoal, setRoutineGoal] = useState('');
    const [isPublic, setIsPublic] = useState(false);
    const [currentUser, setCurrentUser] = useState('');

    useEffect(() => {
      fetchUsername()
      .then((data) => setCurrentUser(data.username))
    })

    useEffect(() => {
      fetchUserRoutines()
      .then((data) => setRoutines(data))
    })

    const useStyles = makeStyles((theme) => ({
        container: {
          display: 'flex',
          flexWrap: 'wrap',
        },
        formControl: {
          margin: theme.spacing(2),
          minWidth: 120,
        },
        textFields: {
            width: '350px',
            margin: theme.spacing(2)
          },
      }));

      const classes = useStyles();


    if(open === true) {
    return (
      <div className='routineform'>
      <FormControl className={classes.formControl}>  
        <form onSubmit={e => {
          e.preventDefault();
          createRoutine({routineName, routineGoal, isPublic});
          alert('Your routine has been created!');
            //need to redirect to /myroutines
        }} >
        <TextField 
          id="outlined-basic" 
          label="Name" 
          variant="outlined" 
          size='small'
          required onChange={(e) => setRoutineName(e.target.value)} 
          value={routineName}/>
        <TextField 
          id="outlined-basic" 
          label="Goal" 
          size='small'
          variant="outlined" 
          required onChange={(e) => setRoutineGoal(e.target.value)} 
          value={routineGoal}/>

        <label className='label'>Allow others to see? </label>
          <Checkbox 
            type='checkbox'
            size='small' 
            value={isPublic}
            color='primary' 
            onClick={() => setIsPublic(true)} />

          <Button 
            color='primary'
            variant="contained"
            type='submit'
            onClick={() => createRoutine}>Create Routine</Button>
          </form>
        </FormControl>
      </div>
    )}
    else return (
        <div></div>
    )
  }

export default RoutineForm;
