import React, { useState } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import { getCurrentToken, storeLoginToken } from '../api';
import './login.css'

const Login = ({username, setUsername, password, setPassword, setCurrentUser}) => {

  const [token, setToken] = useState('');

  const loginUser = async (username, password) => {
    await fetch('http://fitnesstrac-kr.herokuapp.com/api/users/login', {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password
      })
    }).then(response => response.json())
      .then(result => {
        console.log(result);
        const token = result.token;
        console.log('This is your login token', token)
        setToken(token);
        storeLoginToken(token);
        setCurrentUser(result.user.username);
      })
      .catch(console.error);
  };

  const handleClick = (event) => {
      event.preventDefault();
      loginUser(username, password);
  };

  if(token) {
    return <Redirect to = '/myroutines' /> }

    return (
    <div className="login-container">
        <form className="login-form" onSubmit={handleClick}>
        <h2>Login here</h2>
        <FormControl variant="outlined"  className={classes.formControl}>

                <TextField className={classes.textFields}
                     type='text' 
                     value={username}
                     variant="outlined"
                     InputLabelProps={{
                      shrink: true,
                    }} 
                     label='Username' 
                     min='8' max='20' required 
                     onChange={(e) => setUsername(e.target.value) }
                />

                <TextField className={classes.textFields}
                    type='password' 
                    value={password}
                    variant="outlined"
                    InputLabelProps={{
                      shrink: true,
                    }} 
                    label='Password' 
                    min='8' max='20' required 
                    onChange={(e) => setPassword(e.target.value) }
                />
            <div className="login-button">
                <Button 
                  size='large'
                  type='submit'
                  color='primary'
                  variant="contained"
                  onClick={() => handleClick}>Login</Button>
            </div> 

          <a className='register-link' href='/login'>Already have an account? Sign in</a>
          </FormControl>
        </form>
    </div>
    )
}

export default Login;
