import React from 'react';
import { useEffect, useState } from 'react';
import { createRoutine } from './Routines'
import { fetchUsername, getCurrentToken, fetchUserRoutines } from '../api';
import MyRoutine from './MyRoutine';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Checkbox from '@material-ui/core/Checkbox';



const MyRoutines = () => {

    const [routines, setRoutines] = useState([]);
    const [creator, setCreator] = useState('');
    const [routineName, setRoutineName] = useState('');
    const [routineGoal, setRoutineGoal] = useState('');
    const [isPublic, setIsPublic] = useState(false);
    const [currentUser, setCurrentUser] = useState('');
    const [open, setIsOpen] = useState(false);

    useEffect(() => {
      fetchUsername()
      .then((data) => setCurrentUser(data.username))
    })

    useEffect(() => {
      fetchUserRoutines()
      .then((data) => setRoutines(data))
    })


    const showForm = () => {
        if (open === false){
             setIsOpen(true);}
        else {
            setIsOpen(false)
        }
    }

  //  useEffect(() => {
  //   fetchUserRoutines(currentUser)
  //   .then((data) => console.log(data))
  // }, [])
  //unsure of how to get username inside url parameter???

  if(getCurrentToken()) {
    return (
    <div>
      <h1>My Routines</h1>
            {routines ? routines.map((routine, index) => 
            <MyRoutine creator={creator} setCreator={setCreator} key={index} routine={routine} />) : <h1>Use the form above to start creating routines.</h1>}
          <Button 
            color='primary'
            variant="contained"
            type='submit'
            onClick={() => showForm}>Create A New Routine</Button>
         </div>
    )} else return (
          <div className='myRoutines'>
          <h1>Please register or sign in to see your routines.</h1>
      </div>
    )
  }

const routineForm = () => {
  return (
    <div className='myRoutines-card'>

      <form onSubmit={e => {
        e.preventDefault();
        createRoutine({routineName, routineGoal, isPublic});
        alert('Your routine has been created!');
          //need to redirect to /myroutines
      }} >
      <TextField 
        id="outlined-basic" 
        label="Name" 
        variant="outlined" 
        required onChange={(e) => setRoutineName(e.target.value)} 
        value={routineName}/>
      <TextField 
        id="outlined-basic" 
        label="Goal" 
        variant="outlined" 
        required onChange={(e) => setRoutineGoal(e.target.value)} 
        value={routineGoal}/>

      <label>Allow others to see? </label>
        <Checkbox 
          type='checkbox' 
          value={isPublic}
          color='primary' 
          onClick={() => setIsPublic(true)} />

        <Button 
          color='primary'
          variant="contained"
          type='submit'
          onClick={() => createRoutine}>Create Routine</Button>
        </form>
    </div>

  )
}


export default MyRoutines;
