import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import { fetchUsername, fetchUserRoutines } from '../api';
import createRoutine from './Routines'

const RoutineForm = ({open}) => {

    const [routines, setRoutines] = useState([]);
    const [routineName, setRoutineName] = useState('');
    const [routineGoal, setRoutineGoal] = useState('');
    const [isPublic, setIsPublic] = useState(false);
    const [currentUser, setCurrentUser] = useState('');

    useEffect(() => {
      fetchUsername()
      .then((data) => setCurrentUser(data.username))
    })

    useEffect(() => {
      fetchUserRoutines()
      .then((data) => setRoutines(data))
    })

    if(open === true) {
    return (
      <div className=''>

        <form onSubmit={e => {
          e.preventDefault();
          createRoutine({routineName, routineGoal, isPublic});
          alert('Your routine has been created!');
            //need to redirect to /myroutines
        }} >
        <TextField 
          className={routineform}
          id="outlined-basic" 
          label="Name" 
          variant="outlined" 
          required onChange={(e) => setRoutineName(e.target.value)} 
          value={routineName}/>
        <TextField 
          className={routineform}
          id="outlined-basic" 
          label="Goal" 
          variant="outlined" 
          required onChange={(e) => setRoutineGoal(e.target.value)} 
          value={routineGoal}/>

        <label>Allow others to see? </label>
          <Checkbox 
           className={routineform}
            type='checkbox' 
            value={isPublic}
            color='primary' 
            onClick={() => setIsPublic(true)} />

          <Button 
            className={routineform}
            color='primary'
            variant="contained"
            type='submit'
            onClick={() => createRoutine}>Create Routine</Button>
          </form>
      </div>
    )}
    else return (
        <div></div>
    )
  }

export default RoutineForm;
