import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import './style.css';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';
import { createRoutine } from '../api';


const AddActivityForm = ({open}) => {

    const [activityName, setActivityName] = useState('');
    const [activityDescription, setActivityDescription] = useState('');

    const useStyles = makeStyles((theme) => ({
      container: {
        display: 'flex',
        flexWrap: 'wrap',
        position: 'relative'
      },
      formControl: {
        margin: theme.spacing(2),
        minWidth: 120,
      },
      textFields: {
          width: '350px',
          margin: theme.spacing(2)
        },
    }));

    const classes = useStyles();

if(open === true) {
    return (
    <div className='activity-form'>
    <FormControl className={classes.container}>  

          <form onSubmit={e => {
            e.preventDefault();
            createActivity({activityName, activityDescription});
            alert('Your activity has been created!');
            //redirect/refresh page without refreshing
        }} >
        <TextField 
          label='Name'
          variant="outlined" 
          size='small'
          required onChange={(e) => setActivityName(e.target.value)} 
          value={activityName}/>
        <TextField 
          label='Description'
          size='small'
          variant="outlined" 
          required onChange={(e) => setActivityDescription(e.target.value)} 
          value={activityDescription} />

          <Button 
            color='primary'
            variant="contained"
            type='submit'>Create Activity</Button>
          </form>
        </FormControl>
    </div>  

        )
    } else return <div></div>;
}

export default AddActivityForm;
