import React, { useEffect, useState } from 'react';
import { fetchRoutines, getCurrentToken } from '../api';
import Routine from './Routine';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import { fetchUsername, fetchUserRoutines } from '../api';
import './style.css';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles } from '@material-ui/core/styles';



export const createRoutine = async ({routineName, routineGoal, isPublic}) => {
  await fetch('http://fitnesstrac-kr.herokuapp.com/api/routines', {
      method: "POST",
      headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${getCurrentToken()}`
        },
      body: JSON.stringify({
          name: routineName,
          goal: routineGoal,
          isPublic: isPublic
    })
  }).then(response => response.json())
  .then(result => {
      console.log(result);
  })
  .catch(console.error);
}


const Routines = () => {

    const [routines, setRoutines] = useState([]);
    const [creator, setCreator] = useState('');
    const [routineName, setRoutineName] = useState('');
    const [routineGoal, setRoutineGoal] = useState('');
    const [isPublic, setIsPublic] = useState(false);

    useEffect(() => {
        fetchRoutines()
        .then((data) => setRoutines(data))
        console.log(routines)
    }, [])

    if(getCurrentToken()) {
    return (
        <div className='publicRoutines'>
            <h1>Fitness Routines</h1>

            <div className='routineform'>
      <FormControl className={classes.container}>  
        <form onSubmit={e => {
          e.preventDefault();
          createRoutine({routineName, routineGoal, isPublic});
          alert('Your routine has been created!');
            //need to redirect to /myroutines
        }} >
        <TextField 
          id="outlined-basic" 
          label="Name" 
          variant="outlined" 
          size='small'
          required onChange={(e) => setRoutineName(e.target.value)} 
          value={routineName}/>
        <TextField 
          id="outlined-basic" 
          label="Goal" 
          size='small'
          variant="outlined" 
          required onChange={(e) => setRoutineGoal(e.target.value)} 
          value={routineGoal}/>

        <label className='label'>Allow others to see? </label>
          <Checkbox 
            type='checkbox'
            size='small' 
            value={isPublic}
            color='primary' 
            onClick={() => setIsPublic(true)} />

          <Button 
            color='primary'
            variant="contained"
            type='submit'
            onClick={createRoutine}>Create Routine</Button>
          </form>
        </FormControl>
          {routines ? routines.map((routine, index) => <Routine creator={creator} setCreator={setCreator} key={index} routine={routine} /> ) : null }
         </div>
      </div>
    )}
    else return (
        <div className='publicRoutines'>
            <h1>Fitness Routines</h1>
            {routines ? routines.map((routine, index) => <Routine creator={creator} setCreator={setCreator} key={index} routine={routine} /> ) : null }
         </div>
    )
}



export default Routines;
